import React, { useCallback, useEffect, useState } from "react";
import HookBtn from "./components/button";
import Tasks from "./components/Tasks/Tasks";
import NewTask from "./components/NewTask/NewTask";
import useHttp from "./hooks/use-http";
import useToggle from "./hooks/use-toggle";
function App() {
  const [tasks, setTasks] = useState([]);

  const [value, toggleValue] = useToggle(false);

  const transformTasks = useCallback((data) => {
    const loadedTasks = [];

    for (const taskKey in data) {
      loadedTasks.push({ id: taskKey, text: data[taskKey].text });
    }

    setTasks(loadedTasks);
  }, []);
  const { isLoading, error, sendRequest: fetchTasks } = useHttp(transformTasks);

  useEffect(() => {
    fetchTasks({
      url: "https://httppractice-86b7f-default-rtdb.firebaseio.com/tasks.json",
    });
  }, [fetchTasks]);

  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };

  const onToggle = () => {
    console.log("working");
  };

  return (
    <React.Fragment>
      <p style={{ backgroundColor: "wheat" }}>{`Toggle Value : ${value}`}</p>
      <HookBtn btnFx={toggleValue} title={"Toggle"} />
      <HookBtn btnFx={() => toggleValue(false)} title={"False"} />
      <NewTask onAddTask={taskAddHandler} />
      <Tasks
        items={tasks}
        loading={isLoading}
        error={error}
        onFetch={fetchTasks}
      />
    </React.Fragment>
  );
}

export default App;
